sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
			   var now = new GlideDateTime();
				var overdueTask = new GlideRecord('x_58872_needit_needit_task');
				overdueTask.addQuery('due_date','<',now);
				overdueTask.addQuery('state','!=',3);
				overdueTask.addQuery('assigned_to',current.assigned_to);
				overdueTask.query();
				if (overdueTask.getRowCount() > 2){
					template.print('In addition to this overdue task, yuo have ' + (overdueTask.getRowCount() -1) + ' tasks to be completed');
				}

})(current, template, email, email_action, event);
