sysauto_script.script:
var now = new GlideDateTime();
var next24 = new GlideDateTime();
next24.addDaysLocalTime(1);

gs.info("wbo now: " + now.getValue() + ", next24: " + next24.getValue());
var localTime = now.getLocalTime();

var next24Overdue = new GlideRecord('x_58872_needit_needit_task');
next24Overdue.addQuery('due_date','>',now);
next24Overdue.addQuery('due_date','<',next24);
next24Overdue.addQuery('due_date','!=',3);
next24Overdue.query();

while(next24Overdue.next()){
	gs.info('WBO NEXT 24 OVERDUE (to be set priority 2): ' + next24Overdue.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',next24Overdue,'no:'+next24Overdue.number,gs.getUserName(),'wbo_queue');
}
gs.info("WBO FINISH");
